import processColor from "react-native-web/dist/exports/processColor";
import { round } from "../base";
import color from "./color";
import interpolate, { Extrapolate } from "./interpolate";

function red(c) {
  return c >> 16 & 255;
}

function green(c) {
  return c >> 8 & 255;
}

function blue(c) {
  return c & 255;
}

function opacity(c) {
  return (c >> 24 & 255) / 255;
}

export default function interpolateColors(animationValue, options) {
  var inputRange = options.inputRange,
      outputColorRange = options.outputColorRange;
  var colors = outputColorRange.map(processColor);
  var r = round(interpolate(animationValue, {
    inputRange: inputRange,
    outputRange: colors.map(red),
    extrapolate: Extrapolate.CLAMP
  }));
  var g = round(interpolate(animationValue, {
    inputRange: inputRange,
    outputRange: colors.map(green),
    extrapolate: Extrapolate.CLAMP
  }));
  var b = round(interpolate(animationValue, {
    inputRange: inputRange,
    outputRange: colors.map(blue),
    extrapolate: Extrapolate.CLAMP
  }));
  var a = interpolate(animationValue, {
    inputRange: inputRange,
    outputRange: colors.map(opacity),
    extrapolate: Extrapolate.CLAMP
  });
  return color(r, g, b, a);
}
